document.querySelector("#openaiText2SpeechVoice").addEventListener("change",e=>{let t=document.querySelector("#openaiText2SpeechVoicePreview audio");t.pause(),t.currentTime=0,t.setAttribute("src",`https://cdn.openai.com/API/docs/audio/${e.currentTarget.value}.wav`)}),document.querySelector("#openaiText2SpeechSpeed").addEventListener("input",e=>{document.querySelector("label[for=openaiText2SpeechSpeed] span").innerText=parseFloat(e.currentTarget.value).toFixed(2)});